import React ,{useState} from "react";
import './App.css';

const App =() =>  {
  const[list , setList] = useState([]);//array to add todos
  const[todo, setTodo] = useState(""); //for current item in todo

  function handleSubmit(e){
    e.preventDefault();

    const newTodos ={
      text : todo,
      completed: false
    };
    setList([...list].concat(newTodos)); //add the new todo in the list
    setTodo(""); // make the last todo null 
    };

  return (
    <div className="App">
      <form onSubmit ={handleSubmit}>
        <input type="text" onChange={(e)=> setTodo(e.target.value)} value={todo}/>
        <button type="submit" >Add List</button>
      </form>
      {list.map((todo)=> <div> {todo.text}</div>)}
    </div>
  );
};

export default App;
